UPDATE dacuser AS u
SET institution_id = (
SELECT institution_id FROM institution WHERE institution_name = (
SELECT p.propertyvalue FROM user_property p
WHERE p.propertykey = 'institution' AND p.userid = u.dacuserid
)
);
